void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2. - 1.;

    float aspect = iResolution.x / iResolution.y;
    uv.y = uv.y / aspect;

    float size = 4.0;

    float invsize = 1. / size;

    vec3 white = vec3(1.,1.,1.);

    vec3 col = vec3(uv,0.0);

    if ( uv.x < invsize && uv.x > -invsize )
    if ( uv.y < invsize && uv.y > -invsize  ) col = test(uv);


    // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 test( vec2 uv )
{
    //return vec3(exp(sqrt(uv.x * uv.x + uv.y * uv.y)));
	return vec3(pow(sqrt(uv.x * uv.x + uv.y * uv.y),0.4));
}
