void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize according to resolution
    vec2 uv = fragCoord / iResolution.xy;
    // Fix any unequal ratio
    float ratio = iResolution.y / iResolution.x;
    uv.y *= ratio;
    // Move center to center
    vec2 center = vec2(0.5, 0.5 * ratio);
    uv -= center;

    vec2 orig = uv;

    float speed = 0.14 + 0.05 * cos(0.01 * iTime);

    //uv.x = uv.x * cos(1.141 * iTime) + 0.2 * sin(0.44 * iTime);
    //uv.y = fract(2.0 * uv.x - 1.0);
    uv.y += sin(iTime + 28.0 * uv.y) * uv.y + fract(2.0 * uv.x * uv.y - 1.0);
    float rot = 0.4 * sin(iTime) * cos(0.5 * iTime);
    uv.x *= rot * cos(speed * 4.0 * uv.x * iTime);

    // Uncomment for odd hair
    //uv.y *= rot * sin(speed * iTime * 0.4) * sin(speed * 0.4 * uv.y * iTime);

    float delta = 1.0;
    //delta += 0.9 * sin(0.5 * iTime);
    float r = 0.0, g = 0.1, b = 0.3;

    float circle = length(uv);
    float radius = 0.16 + 0.1 * sin(speed * iTime);
    float scale = dFdx(uv.x);
    float col = float((circle - radius) / scale > 0.0 ? 1.0 : 0.0);

    if (iMouse.z > 0.0) circle = length(uv + abs(sin(speed * iTime * uv * uv)));
    radius = 0.1 + 0.1 * cos(speed * iTime) + sin(uv.y*64.0+iTime*3.0)*0.01;
    float colb = float((circle - radius) / scale > 0.0 ? 1.0 : 0.0);

    r += delta * col;
    g += delta * (col + colb) / 2.0;
    b += delta * colb;

    circle = length(orig);
    radius = 0.16 + 0.4 * sin(speed * iTime);
    float boringCirc = float((circle - radius) / scale > 0.0 ? 1.0 : 0.0);

    if (boringCirc > 0.0) r = g = b = 1.0;

    fragColor = vec4(r, g, b, 1.0);
}
