// golfing no-AA still version of https://shadertoy.com/view/WlXXWj
// ( which was a fork of https://www.shadertoy.com/view/Mt2fzR )

// 130 chars    -8 by iapafoto -2 by mathmasterzach

void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy,
         p = ( u+u-R ) / R.y + 1., k;
    for(; ++O.w<7.; R /= k )
        p *= k.y = 1.5 / dot( p= mod(++p,2.) -1., p );
    O += abs(R*p).y;
} /*



