const float pi = acos(-1.);
const float tau = pi*2.;


float graphx(float x)
{    
    float r = fract(x);
    float t = r*2.-.5;
    float n = 9.;
	float f = 1.
        *step(t,.5)
        *abs(sin(x*tau*n))
        *exp(-t*t*n)
        ;

    return f/8.;
}

float graphy(float x)
{
    float r = fract(x*3.3);
    float t = r*2.-.5;
    float n = 4.;
    float k = 1.;
    float f = 1.
        *step(t,.5)
        *(sin(r*n*tau))
        *exp(-t*t*n/k)
        ;
	return f/8.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord-.5*iResolution.xy)/min(iResolution.x,iResolution.y);

    float t = .5/iResolution.y;
    vec3 col = vec3(0.);
    float d = 0.;

    float time = iTime/5.;


    float func = graphx(p.x/2.+time); 
    col.g += smoothstep(t,.0,abs(p.y-func)-t);

    func = graphy(p.y/2.+time+.15);
    col.r += smoothstep(t,.0,abs(p.x-func)-t);

    float x=graphy(time+.15),y=graphx(time);
    d = length(p-vec2(x,y))-.015;
    d = smoothstep(t,.0,d);
    col += mix(col,vec3(1),d);

    fragColor = vec4(col,1.0);
}
