#define B(a) max(a.x,max(a.y,a.z))               // box

void mainImage(out vec4 O, vec2 U) {

    float T = iTime, t=1.,c=0.;
    mat2  R = mat2( cos(.3*T +vec4(0,33,11,0)) );// rotation
    vec3  q = iResolution, a,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),       // ray direction
          p = 30./q;                             // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p.zxy,
        q.xy *= R,
        a = abs(q), t = abs(B(a)-6.)-.5,         // hollow cube
        a.z = ( mod(q.z-1.5,3.)-1. )*6.,         // windows: repeat + sym
        a = abs(a-vec3(4,4,0)),
        t = max(t,  -(B(a)-3.) ),                // carve windows
      //q.xy *= R,
        a = abs(q), t = min(t, c=B(a)-3.),       // add inner cube
        p += .7*t*D;                             // step forward = dist to obj
    c==t ? O*=vec4(.5,0,0,1) : O;                // color inner cube
}
