void mainImage(out vec4 O, vec2 C) {
	O-=O;
	for(float s,k,g,e,i=0.;i++<45.;){
        vec3 p=g*normalize(vec3((C*2.-iResolution.xy)/iResolution.y,2));
        p.z+=iTime;
        p.xz=fract(p.xz)-.5;
        k=1.;
        for(int i=0;i++<9;)
            s=2./clamp(dot(p,p),.1,1.),
            p=abs(p)*s-vec3(.5,3,.5),
            k*=s;
        g+=e=length(p)/k-.001;
        e<.01?O.xyz+=cos(vec3(7)+p*.1)/i:p;
	}
}
