float RayMarch(vec3 ro, vec3 rd) 
{

    // The extra distance might force a near-plane hit, so
    // it's set back to zero.
    float dO = 0.; 
    //Determines size of shadow
    for(int i=0; i<MAX_STEPS; i++) 
    {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);

        // The "abs" is a hack of sorts, but it forces the ray to hone in
        // on the surface from either side. "dS*dS<SURF_DIST*SURF_DIST"
        // would also work.
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;

        // This needs to be after the surface test. Otherwise, you could
        // just the ray back out of range.
        dO += dS;
        // Ray shortening, for more accuracy, if you need it. It might be
        // hacky, but it's necessary sometimes.
        //dO += dS*.85; //  dS*.7, etc.
    }

    return dO;
}




