mat2 Rot (float a)
{
 float s = sin(a);
 float c = cos(a);
 return mat2(c,-s,s,c);      
}

float hex (vec2 p)
{
 p = abs(p);
 //p *= (Rot ((((floor(p.y*10.)))*0.5+sin(iTime))*1.));
 //p *= (Rot ((((floor(p.x*5.)))+cos(iTime))*.5));
 float c = dot(p,normalize(vec2(1,1.73)));
 c = max(c,p.x);
 return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
 uv *= 1.;   

// uv = abs (uv)-0.5;   

uv *= (Rot ((sin(iTime)*+1.5)));


 float c = tan(hex(uv)*2.+iTime);   
 vec3 col = vec3(step(c,.1));
 fragColor = vec4(col,1.0);
}
