// Fork from https://www.shadertoy.com/view/XsfGzn

#define BLUR_AMOUNT 0.007

float random(vec3 scale,float seed){return fract(sin(dot(gl_FragCoord.xyz+seed,scale))*43758.5453+seed);}

vec4 boxBlur(sampler2D tex, vec2 uv, vec2 delta){
    vec4 color=vec4(0.0);
	float total=0.0;
	float offset=random(vec3(12.9898,78.233,151.7182),0.0);
	for(float t=-30.0;t<=30.0;t++){
		float percent=(t+offset-0.5)/30.0;
		float weight=1.0-abs(percent);
		vec4 bsample=texture(tex,uv+delta*percent);
		bsample.rgb*=bsample.a;
		color+=bsample*weight;
		total+=weight;
	}

	color=color/total;
	color.rgb/=color.a+0.00001;

    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;

	vec3 bg = texture( iChannel0, q ).xyz;
	vec3 fg = texture( iChannel1, q ).xyz;
	vec3 fgBlur = boxBlur( iChannel1, q, vec2(BLUR_AMOUNT) ).xyz;


    float maxrb = max( fg.r, fg.b );
    float k1 = clamp( (fg.g - maxrb)*5.0, 0.0, 1.0 );

    float maxrbBlur = max( fgBlur.r, fgBlur.b );
    float k2 = clamp( (fgBlur.g - maxrbBlur)*5.0, 0.0, 1.0 );


	float ll = length( fg );
    fg.g = min( fg.g, maxrb*0.8 );
    fg = ll*normalize(fg);



   fragColor = vec4( mix(fg, bg, k2), 1.0 );
}
