float maxStrength = 0.0125;
float minStrength = 0.005;

void mainImage(out vec4 fragColour, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    //--- Strength animate ---
    maxStrength = clamp(sin(iTime/2.0),minStrength,maxStrength);
    //-----------------------

	//--- Colour ---
    vec3 colour = vec3(texture(iChannel0, uv));
    colour.r = texture(iChannel0, vec2(uv.x+maxStrength,uv.y+maxStrength)).r;
    //--------------

    fragColour = vec4(colour,1.0);
}
