void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
  float PI = 3.14159265359;
  vec2 t = (fragCoord - iResolution.xy/2.)/iResolution.y;
  float zoom = 30.;
  t *= zoom;
  float b = floor(length(floor(t)));
  t = fract(t)-.5;
  float z = mod(iTime, b*2.)-b;
  float a = (1. + sign(z))/2.;
  float e = max(2.*fract(iTime)-1., 0.);
  float rad = floor(abs(z))+e*a+(1.-e)*(1.-a);
  rad = -rad * PI/2. - PI/4.;
  t = mat2(cos(rad), sin(rad), -sin(rad), cos(rad))*t;
  float r = zoom*1.5/iResolution.y/2.;
  float c = smoothstep(-r, +r, t.y);
  fragColor = vec4(vec3(c), 1.);
}
