float julia(vec2 uv, vec2 c) {
    int i;
    for (i = 0; i < 100; i++) {
        uv = vec2(uv.x * uv.x - uv.y * uv.y + c.x,
                  2.0 * uv.x * uv.y + c.y);
        if (length(uv) > 100.0) {
            break;
        }
    }
    return float(i) / 100.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = -1.0 + 2.0 * fragCoord / iResolution.xy;

    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;

    uv *= pow(0.5, -1.0 + 30.0 * (0.5 + 0.5 * sin(iTime * 0.20 - (3.14159265))));
    uv += vec2(-0.51, -0.61351); // From iq
    float f = julia(vec2(0.0, 0.0), uv);

    // Output to screen
    fragColor = vec4((1.0 - uv) * pow(f, 0.5), f, 1.0);
}
