# define ttime floor(iTime*.2) + pow(fract(iTime*.2),1.25)

mat2 r(float a){ return mat2(cos(a),sin(a),-sin(a),cos(a));  }


vec3 fig(vec2 uv, float p) {
    float d =abs(atan(uv.x,uv.y))+3.141592;
    return floor(p*vec3(d)/(3.141592*2.))/p;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy-.5* iResolution.xy)/iResolution.y;

    uv *=r(3.14*.85);

    uv*=4.;
    vec3 a = fig(uv*r(3.1415/4.+cos(iTime)*.1)+vec2(0.5,0.5),10.+sin(ttime));
    vec3 b = fig((uv*r(3.1415*4.+sin(iTime)*.1))-vec2(0.5,0.5),10.+cos(ttime));

     vec3 c = fig(uv*r(3.1415*4.+sin(iTime)*.1)+vec2(0.5,0.5),10.+cos(ttime));
    vec3 d = fig((uv*r(3.1415/4.+cos(iTime))*.1)-vec2(0.5,0.5),10.+sin(ttime));

    vec3 kk = smoothstep(0.69,.75+sin(iTime)*.01,(b+a+c+d)/4.);
    kk = mix(vec3(0.9),vec3(0.1,0.2,0.3),kk);
    fragColor = vec4(kk,1.0);
}
