// idea from https://www.desmos.com/calculator/64ebpdhpzq

#define TAU 6.2831853

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y) * 12.;

    float t = (fract(iTime) - .5) * TAU;
    float s = tan(tan(sin(p.x) * sin(p.y - t)));
    float w = fwidth(s) * .5;
    s = smoothstep(-w, w, p.y - s);

    fragColor = vec4(vec3(s), 1.);
}
