void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(1.0, 1.0, 1.0);
    vec2 uv = fragCoord/iResolution.xy;
    int block = int(9.0f * iTime) % 24 - 12;
    if (block == 0)
    {
        block = 1;
    }
    block = block * 10;
    int result = int((fragCoord.x - iResolution.x / 2.0) * (fragCoord.y - iResolution.y / 2.0)) / block;
    if( result % 3  == 0)
    { 
      col = (0.5 + 0.5*cos(iTime+uv.xyx+vec3(1,3,5)));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
