void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  fragCoord/iResolution.y;

    float size = 0.25 + sin(iTime * 1.25) * 0.125;

    float tetha = distance(uv, vec2(sin(iTime), cos(iTime))*.25 + 0.5 );
    float delta = fwidth(tetha);

    // the old me
    // float alpha = smoothstep(size - delta, size, tetha);
    // float size2 = size - delta * 1.5;
    // float alpha2 = smoothstep(size2 - delta, size2, tetha);
    // fragColor = vec4(vec3(alpha2 - alpha), .625);

    // the @fabriceNeyret guided me
    fragColor = vec4( sqrt( smoothstep(1., 0., abs(tetha-size)/delta ) ) );
}
