mat2 r2(float a) {
	float s = sin(a), c = cos(a);
	return mat2(c,-s,s,c);
}

vec4 r4(float n) {
	#define R4P 1.1673039782614187
    return fract(.5+vec4(1./R4P,1./R4P/R4P,1./R4P/R4P/R4P,1./R4P/R4P/R4P/R4P)*n);
}
vec4 hash(vec4 a) {
    a = mod(abs(a),8273.97234);
    #define R4S(sw) floor(fract(.352347+dot(a,vec4(.001,.1,10.,100.).sw*2.23454))*20000.)
    return r4(R4S(xyzw)+R4S(yzwx)+R4S(wxyz)+R4S(zwxy));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #define time iTime
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;

	float s = 0.;
	for (float p = 0.; p < 1000.; p++) {
		vec4 rh = hash(p*vec4(.01,.1,1.,10.));
		rh.xy = rh.xy*2.-1.;
		float lt = pow(fract(length(rh.xy)+time*.05),.7)*2.;
		rh.xy = normalize(rh.xy)*lt;
		vec2 sp = rh.xy;
		rh.xy *= cos(sp.yx*rh.zw*10.);

		s += max(0.,1.-max(0.,length(uv.xy-rh.xy)-.02)*100.);
	}
	fragColor = mix(mix(vec4(.02,.03,.08,1),vec4(1.,.96,.8,1),pow(clamp(s*.4,0.,1.),2.)),texelFetch(iChannel0,ivec2(fragCoord),0),.95);
}
