void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uv_normal = uv*2.0 - 1.0;
    float dist = length(uv_normal - vec2(0.0));

    vec3 color = texture(iChannel0, uv - uv_normal*dist*0.01).rgb;

    float distance = length(fragCoord.xy - iMouse.xy);
    if (distance < 10.0 && iMouse.z > 0.01) {
        color = vec3((1.0+sin(iTime))/2.0, (1.0+sin(0.85+iTime))/2.0, (1.0+sin(1.70+iTime))/2.0);
    }

    fragColor = vec4(color, 1.0);
}
