float gridSize = 1.0 / 8.0;

float rand(vec2 co) {
    return fract(sin(dot(co.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

float genColor(vec2 pos) {
    vec2 gridPos = floor(pos * gridSize) * gridSize;

    float col = rand(gridPos);

    if (col < clamp(iMouse.x / iResolution.x, 0.01, 0.99))
        col = 0.0;
    else
        col = 1.0;

    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 pos = fragCoord.xy + vec2(iTime * rand(vec2((floor(fragCoord.y * gridSize) * gridSize)) + 1.0) * 800.0, 0.0);

    float alpha = genColor(pos);

    pos += vec2(1.25, 0.0);
    float red = genColor(pos);

    fragColor = vec4(red, alpha, alpha, red);
}
