#define Main void mainImage( out vec4 Q, in vec2 U )

#define Me Q = A(U);

#define Neighbors vec4 n = A(U+vec2(0,1)), e = A(U+vec2(1,0)), s = A(U-vec2(0,1)), w = A(U-vec2(1,0));

#define Mean vec4 m = 0.25*(n+e+s+w);

#define Diffuse Q += vec4(.5,0,1,0)*(m-Q);

#define Grow float g = .5*Q.z*Q.x*(1.-Q.x);

#define React Q.x += g-.05; Q.z += .1*(1.-Q.z)-g;

