void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = texture(iChannel0, uv);

    fragColor = vec4(col.xyz, 1.0);
}

float sample_tex(vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    vec4 col = texture(iChannel0, uv);
    return round(col.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float cur_state = sample_tex(fragCoord);

    float ne = sample_tex(fragCoord+vec2(-1.0, 1.0)),
    	  n = sample_tex(fragCoord+vec2(0.0, 1.0)),
          nw = sample_tex(fragCoord+vec2(1.0, 1.0)),
          e = sample_tex(fragCoord+vec2(-1.0, 0.0)),
          w = sample_tex(fragCoord+vec2(1.0, 0.0)),
          se = sample_tex(fragCoord+vec2(-1.0, -1.0)),
          s = sample_tex(fragCoord+vec2(0.0, -1.0)),
          sw = sample_tex(fragCoord+vec2(1.0, -1.0));

    float count = ne + n + nw + e + w + se + s + sw;

    if (cur_state == 1.0 && count != 2.0 && count != 3.0)
        cur_state = 0.0;
    else if (count == 3.0)
        cur_state = 1.0;

    vec3 col = vec3(cur_state);

    if(iFrame < 10 || iMouse.z > 1.0 && distance(fragCoord, iMouse.xy) < 25.0) {
        vec2 uv = fragCoord / iResolution.xy;
        uv = fract(uv + vec2(iTime * 0.01));
        col = vec3(texture(iChannel1, uv).x > 0.6);
    }

    fragColor = vec4(col.xyz, 1.0);
}
