#define SPEED 6.0
mat3 m = mat3( 0.00,  0.80,  0.60,
              -0.80,  0.36, -0.48,
              -0.60, -0.48,  0.64 );
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    vec2 uv = (p.xy+vec2(670.0,1.0)*p.z) + f.xy;
    vec2 rg = textureLod( iChannel0, (uv+ 0.5)/256.0, 0.0 ).yx;
    return mix( rg.x, rg.y, f.z );
}



float fbm(vec3 v)
{
    float frequency = 1.0;
    float gain = 1.0;
    float damp = 0.5;
    float lacunarity = 1.81;
	float sum = 0.0;
    for (int i = 0; i < 6; i++)
    {
        sum += (1.0 - pow(abs(noise(v * frequency)), 2.0) )* (gain);
        frequency *= lacunarity;
        gain *= damp;
        v*=m;
    }
    return sum * 0.75;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 rd = normalize(vec3((fragCoord.xy-0.5*iResolution.xy)/iResolution.y, 1.));
	vec3 ro = vec3(0., 0., -2.);
    vec3 pos = vec3(ro.x, ro.y, ro.z += (iTime * SPEED));
    pos.xz += iMouse.xy;
    float sum = 0.0;
    float t = 0.0;
	for (int i = 0; i < 150; i++)
    {   
        pos += (rd * t);
        float n = abs(fbm(pos / 32.0));
        sum += (n);
        t = (n);
    }
    sum /= 500.0;
    float blend = smoothstep(0.25, 0.5, sum * 1.1);
    float fog = mix(0.0, 1.0, blend);
    fragColor = vec4(fog + vec3(0.0, 0.2, 0.5), 1.0);
}
