//Pokeables
#define ZOOM 8.
#define POWER 16.


#define PI acos(-1.0)
#define sinc(x) sin(PI*x)/(PI*x)

float motherWavelet(in float t, in float a, in float b) {
    return a * sinc(a*t) - b * sinc(b * t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ZOOM * (fragCoord/iResolution.xy - vec2(.5));
    uv.x = uv.x * ( iResolution.x / iResolution.y);
    vec2 wx = POWER * (iMouse.xy/iResolution.xy - vec2(.5));

    if(iMouse.xy == vec2(0.)) {
     //Prevent boring black preview
     wx.x = .5;
     wx.y = .3;
    }


    float L = 1. * motherWavelet(length(uv), wx.x, wx.y);
    float K = 1. * motherWavelet(uv.x, wx.x, wx.y);
    float J = 1. * motherWavelet(uv.y, wx.x, wx.y);


    float V = K * J;

    //TRINITRON-esque
    vec3 col = 3. * vec3(K + J, K - J, -K + J);

    //Blue zone
    //vec3 col = 1. * vec3(K + J, K * J, 1. / (K + J));

    //TRON
    //vec3 col = vec3(V * K, V * J, V / (K + J));

    // TEST CARD
    //vec3 col = vec3(J, K, L);

    //HARSH REALM
    //float th = atan(K, J);
    //float rho = atan(L, (K + J));
    //vec3 col = vec3(sin(rho), cos(th), atan(th, rho));

    // Output to screen
    fragColor = vec4(col,1.0);
}
