// Fork of https://www.shadertoy.com/view/3ttSz4

#define T(V)  ( T = texture(iChannel0, V-vec2(acos(U.x/.9),0)), \
                T * T * smoothstep( w, -w, abs(V).y - .4 )   )

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y, D;
    vec4 T,S;
    float y = .3* ( 1. - U.x*U.x/.8 ), w = 2./R.y;
    O -= O;
    if ( y > 0. )
        D = vec2(iTime,sqrt(y)), 
        O = T(U-D),
        T = T(U+D),
        U.x < 0. ?  S=O,O=T,T=S : O ,
        y = 1. - .7*abs(U.x),               // also nice without abs
        O =  O / y + (1.-O.a) * T * y;
}
