void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{

    vec2 uv = (iResolution.xy - 2. * fragCoord.xy) / iResolution.y;
    int samp=0;
    float f = mainSound( samp,-iTime).x;

    vec3 color2 =uv.y + .5 * cos(tau * asin(uv.x) + vec3(0, f * 1.57, f * 3.14) + iTime);


    vec3 color=uv.y +  vec3(f+0.2, f * 2.57, f * 0.14);

    fragColor.rgb = 1.0 - sqrt(color+color2);

    // fragColor.rgb = vec3(mainSound( in int samp,iTime), 1) * 4.; // for debug
    fragColor.a = 1.; 
}

