void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uint decoded = floatBitsToUint(texelFetch(iChannel0, ivec2(fragCoord), 0).x);

    vec3 col = vec3(0.0);

    if ((decoded>>shift)==NUM)
        col = vec3(1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define NUM 1u
// try anything lower than 23u
#define shift 0u

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uint encoded = NUM << shift;
    fragColor = vec4(uintBitsToFloat(encoded), 1.0, 1.0, 1.0);
}
