void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).xyz;

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy + 0.9*(vec2(fract(iTime), fract(iTime*0.3)) - 0.5))/iResolution.xy;
    float k = uv.x * 4.0;
    float Xn = texture(iChannel0, vec2(uv.x, 0.0)).x;
    if (iTime <= 0.05) Xn = 0.5;
    int iterPerFrame = 22;
    vec3 col = texture(iChannel0, uv).xyz;

    if (uv.y <= 0.005){
        for(int i = 0; i < iterPerFrame; i++) Xn = k * Xn * (1.0-Xn);
        col = vec3(Xn);

    }else{
        for(int i = 0; i < iterPerFrame; i++){
            col += max(0.0, -abs(uv.y - Xn)+0.0009) * 10.0;
            Xn = k * Xn * (1.0-Xn); //iterate to the next pop size
        }
    }
    fragColor = vec4(col,1.0);
    // 0 < k < 4
}
