float pin(vec2 p, float h, float r0, float r1) {

    p = abs(p);

    if (p.x > p.y) p = p.yx;

    vec2 q;

    vec2 b = vec2(r0,0);
    q = p-b;
    float pin = -length(q)+r1;

    vec2 a = vec2(r0-sqrt(r1*r1-h*h),h);


    q = p-a;


    vec2 c = a-b;
    c = vec2(c.y,-c.x);
    float f = min(q.x,dot(q,c));
    if (f > 0.0) {
        // corners
        pin = length(q);
    }

    float r = r0/sqrt(2.0);
    float e = r0*0.5-sqrt((r1*r1-r*r))*sqrt(0.5);

    c = p-b;
    c = vec2(c.y,-c.x);

    float d = dot(p-e,c);

    if (d < 0.0) {
        pin = -length(p-e);
    }

    // outer box
    pin = max(pin,p.y-h);

    return pin;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float scale = 2.0;
    vec2 uv = scale*(fragCoord*2.0-iResolution.xy)/iResolution.y;
    float aa = iResolution.y/scale*0.4;

    float len = 10000.0;

    len = min(pin(uv, 0.9+sin(iTime)*0.1,1.8+sin(iTime*0.89)*0.1,1.6+sin(iTime*0.97)*0.1),len);

    vec3 col = vec3(1.0) - sign(len)*vec3(0.1,0.4,0.7);
    col *= 1.0 - exp(-2.0*abs(len));
    col *= 0.8 + 0.2*cos(120.0*abs(len));
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,1.0/aa,abs(len)) );

    //vec2 a = fract(uv*5.0);
    //col += max(max(a.x,a.y)-0.8,0.0)*5.0;

    // Output to screen
    fragColor = vec4(sqrt(col),1);
}
