renderpass.code:
struct Circle {
 	vec4 color;
    vec2 coord;
    float r;
};

struct Line {
	vec4 color;
    vec2 coord;
    float size;
    float angle;
};

vec4 draw_circle(vec4 fragColor, vec2 uv, Circle c)
{
    float res = (pow(uv.x - c.coord.x,2.0)+ pow(uv.y - c.coord.y,2.0));
    if( abs(res-c.r*c.r) <= (abs(cos(iTime))*0.008 + 0.005)) {
   		fragColor = c.color; 
    }

    return fragColor;
}

vec4 draw_line(vec4 fragColor, vec2 uv, Line line) 
{
    vec2 target;

    target.x = cos(line.angle)*line.size + line.coord.x;
    target.y = sin(line.angle)*line.size + line.coord.y;


    float coeff = (target.y - line.coord.y)  / (target.x - line.coord.x);
 	float b = line.coord.y - line.coord.x * coeff;    

    if(abs(uv.x * coeff + b - uv.y) <= 0.012 * min(uv.x,line.coord.x)/max(uv.x,line.coord.x)) {

        if(target.x >= line.coord.x && uv.x >= line.coord.x && uv.x <= target.x)  {   
        	fragColor = line.color;
        }
        else if(target.x <= line.coord.x && uv.x <= line.coord.x && uv.x >= target.x) {
            fragColor = line.color;
        }
    }

	return fragColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 black = vec3(.0,.0,.0);
    vec4 white = vec4(1.0,1.0,1.0,1.0);
    float speed = (540.0/260.0);

    // Output to screen
    fragColor = vec4(black,1.0);
    fragColor = draw_circle(fragColor,uv,Circle(white,vec2(0.5,0.5),0.4)); 

    fragColor = draw_line(fragColor,uv,Line(white,vec2(0.5,0.5),0.25,-iTime/speed));
}
