float outLine(vec2 p, float dx, float dy)
{
    vec2 uv = (p.xy + vec2(dx , dy )) / iResolution.xy;
    vec4 g = vec4(texture(iChannel0, uv.xy).rrr,1.);
	vec4 c = vec4(vec3(g.r + g.b + g.g)/1.5,1.);
    return c.r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uvOut = iResolution.xy/ iResolution.y;
    vec2 p = fragCoord.xy;
    float gx = .0;
    gx += -1.0 * outLine(p, -.5, -.5);
    gx += -2.0 * outLine(p, -.5,  .0);
    gx += -1.0 * outLine(p, -.5,  .5);
    gx +=  outLine(p,  .5, -.5);
    gx +=  2.0 * outLine(p,  .5, .0);
    gx +=  outLine(p,  .5,  .5);
    float gy = .0;
    gy += -1.0 * outLine(p, -1.0, -1.0);
    gy += -2.0 * outLine(p,  0.0, -1.0);
    gy += -1.0 * outLine(p,  1.0, -1.0);
    gy +=  outLine(p, -1.0,  1.0);
    gy +=  2.0 * outLine(p,  0.0,  1.0);
    gy +=  outLine(p,  .5,  .5);
    //float g = abs(sin((gx*gx + gy*gy)*(iTime*.15))*1.25);
    float g = gx*gx + gy*gy;
    vec4 col = vec4(.31,.31,.31,1.);
    col += vec4(g, g, g, 1.0);
    fragColor = col;
}
