void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 nUV = fragCoord / iResolution.xy;

    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.x;
	uv.x += cos(iTime) * 0.3;
    uv.y += -sin(iTime) * 0.3;

    int tx = int(nUV * 512.0);
    int ty = int(nUV * 512.0);

    float wave = texelFetch(iChannel0, ivec2(ty, 1), 0).x;

    float a = atan(uv.x/uv.y) + sin(0.25 / length(uv));
    float r = length(uv);

    vec2 polar = vec2(0.25 / r + iTime, (a / (2.0 * 3.14)));

    float d = step(0.1, abs(wave - nUV.y));

    vec3 tunnelCol = texture(iChannel1, polar).xxx * pow(length(uv), 0.8);

    // Output to screen
    fragColor = vec4(mix(tunnelCol, vec3(uv, 0.5), 1.0 - d),1.0);
}
