float test_method(float x) {
    return cos(x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	float start = radians(43.44);
	float end = radians (43.45);   
    float res_start = test_method(start);
    float res_end = test_method(end);
    float x = mix(start, end, uv.x);
    float s = test_method(x);

    float col = mix(res_start, res_end, uv.y)>s ? 1.0 : 0.0;

    fragColor = vec4(col,col, col, 1.0);
}
