void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(1., .0, 0.);
    vec3 col2 = vec3(1., 1., 0.);
    float y = 1.-uv.y;

    float v =2.;
    v = abs(sin(iTime*10.))*0.2+1.8;
    float n = 2.;
    float w = 0.5;
    uv.x = fract(uv.x*n);

    uv.y += 0.1;
    uv.y /= v/2.;

    float d = 1.-uv.y;
    float x = uv.x - 0.5;
    x *= max(v * cos(x), 1.);
    d *= cos(x*3.14);


    col *= d;
    col2 *= d * d/cos(d);
    col2 *= 1.-floor(uv.y+0.1);
    col += col2;

    fragColor = vec4(col,1.0);
}
