#define PI 3.14159265358979
#define E 2.718281828459
float normsin(float x) {return .5+.5*sin(x);}

vec3 color(float x){//x 0 to 1
	x=abs(sin(x));
    //bezier 
    float r=(1.0-x)*(1.0-x);
    float g=x*x;
    float b=2.0*(1.0-x)*x;
    return vec3(r,g,b);
}




float ln(float x){
	return log(x)/log(E);
}

//new complex function for b^p where b is real and p is complex
vec2 exponent(float b,vec2 p){
    float v= ln(b)*p.y;
	return pow(b,p.x)*vec2(cos(v),sin(v));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //frame
    //make camera rotate with power
    float zoom=1.0;//intial zoom
    float zoomer=1.0/60.0;//governs the speed of zoom
   	const vec2 center=vec2(-1,0);//where the camera is centered
    vec2 uv = 2.0/(zoom*exp2(zoomer))*((2.*fragCoord-iResolution.xy)/iResolution.y)+center;

    const int maxiter=400;
    float count=0.0;
    vec2 value=vec2(0);
	float bound= 150.0;


    for(int i=0;i<maxiter;i++){
        count+=1.0;
        //change these to change plot
        //value=square(value)+uv
        //value=power(value,2.0)+uv;
        value=exponent(float(iFrame)/200.0,value)+uv;
    	if(length(value)>bound)//bounary
            break;
    }



    //printing
    const float cycleRate=3.0;//how fast colors change
    const float colorShift=.12;
    vec3 col=vec3(0);
    if (length(value)>2.0){
        col=color(float(count)*cycleRate+colorShift);
    }
    fragColor=vec4(col,1);



}
