void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.);
    float t = 1.5/iResolution.y;
    float k = 3.;
    float d = 0.;
    for(float f,i = -.108; i < .108; i+=.008)
    { 
	  f = round((i-p.y)*125.)/125.;
      float x = p.x + cos(f*9.)*.25;
      x = x*k;
      float y = p.y+f;
      y += -cos(x+f*18.+ iTime*.5)*smoothstep(1.,.0,abs(x))*.1;
      float m = smoothstep(t,-t,y);
      d = mix(d,m,m);
    }

    col += d;

    fragColor = vec4(col,1.0);
}
