#define R iResolution.xy
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
vec4 B (vec2 U) {return texture(iChannel1,U/R);}
void mainImage( out vec4 Q, in vec2 U )
{
    vec4 
        n = B(U+vec2(0,1)),
        e = B(U+vec2(1,0)),
        s = B(U-vec2(0,1)),
        w = B(U-vec2(1,0));
    vec2 g = vec2(e.z-w.z,n.z-s.z);
    Q = (1.-g.x)*texture(iChannel2,A(U).xy/R);
}

#define R iResolution.xy
vec4 F (vec2 U) {return texture(iChannel2,U/R);}
float ln (vec2 p, vec2 a, vec2 b) { // returns distance to line segment for mouse input
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}
vec4 T (vec2 U) {
    U -= F(U).xy;
    U -= F(U).xy;
    return texture(iChannel0,U/R);}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = 0.25*(T(U+vec2(1,0))+T(U-vec2(1,0))+T(U+vec2(0,1))+T(U-vec2(0,1)));


    if (U.x<2.||U.y<2.||R.x-U.x<2.||R.y-U.y<2.||iFrame < 1) Q = vec4(U,0,0);
}

// Fluid
vec2 R;
float ln (vec2 p, vec2 a, vec2 b) { // returns distance to line segment for mouse input
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}
vec4 A (vec2 U) {return texture(iChannel2,U/R);}
vec4 t (vec2 U) { // access buffer
	return texture(iChannel0,U/R);
}
vec4 T (vec2 U) {
    // sample things where they were, not where they are
	U -= 0.5*t(U).xy;
	U -= 0.5*t(U).xy;
    return t(U);
}
void mainImage( out vec4 C, in vec2 U )
{
   R = iResolution.xy;
   C = T(U);
   #define o 1.
   vec4 // neighborhood
        n = T(U+vec2(0,o)),
        e = T(U+vec2(o,0)),
        s = T(U-vec2(0,o)),
        w = T(U-vec2(o,0));
   // xy : velocity, z : pressure, w : spin
   C.x -= 0.25*(e.z-w.z-C.w*(n.w-s.w));
   C.y -= 0.25*(n.z-s.z-C.w*(e.w-w.w));
   C.z  = 0.25*((s.y-n.y+w.x-e.x)+(n.z+e.z+s.z+w.z));
   C.w  = 0.25*((s.x-n.x+w.y-e.y)-(n.w+e.w+s.w+w.w));
   C.xy *= .999;
   C.xy -= .001*(U-A(U).xy);
   // boundary conditions
   if( U.x < 2. || U.y < 2. || R.x-U.x < 2. || R.y-U.y < 2.)
        C.xy *= 0.;
   vec4 mo = texture(iChannel1,vec2(0));
   float l = ln(U,mo.xy,mo.zw);
   if (iMouse.z>0.) C.xy = mix(C.xy,7.*(mo.xy-mo.zw)/R.y,smoothstep(20.,0.,l));
}

