{   R = iResolution.xy;
 		vec4 M = iMouse;
        vec4 p = T(U);
    	if (M.z>0.) {
        	if (p.z>0.) C =  vec4(M.xy,p.xy);
    		else C =  vec4(M.xy,M.xy);
