float circle(in vec2 _st, in float _radius){
    vec2 dist = _st-vec2(0.5);
	return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(dist,dist)*4.0);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st = (fragCoord/2.1  - 0.1 * iResolution.xy) / iResolution.y+0.35;

    float pct = 1.8-st.y*1.6;

    for(int i=0; i<50;i++){
        //change i to float so I can multiply by it
        float i_float = float(i);
    	pct += circle(st+(0.6*sin(iTime/3.-i_float*st.yx)),0.01)*st.y*1.5;
    }


	pct=pct-1.;

    // Output to screen, adjust colour
    fragColor = vec4(pct+.1,pct+.1,pct,1.0)*vec4(0.9,0.,0.,0.)+vec4(0.,0.0,0.3,0.);
}
