/* Creative Commons Licence Attribution-NonCommercial-ShareAlike 
   phreax 2019
*/

#define rot(a) mat2(cos(a), sin(a), -sin(a), cos(a))
#define PI 3.141592
#define T1 118.9
#define T2 134.2
#define BEATS 15.53
#define BEAT_OFF 6.88
#define time iChannelTime[0]
#define S(a, b, x) smoothstep(a, b, x)

// interval, 1 if x in [a, b], 0 otherwise
#define I(a, b, x) step(a, x)*step(x, b)


// fadeIn in only defined in interval [a, b]
float fadeInt(float a, float b, float x) {
    return S(a, b, x)*I(a, b, x);
}

// sum average fft for bands
float avgVolume(int fMin, int fMax) {
    float sum = 0.0;

    for(int i = fMin; i <= fMax; i++) {
        sum += texelFetch(iChannel0, ivec2(i, 0), 0).x;
    }

    return sum / float(fMax-fMin);
}

float layer(vec2 uv, vec2 sound) {
    float bass = sound.x;
    float mid = sound.y;

    uv = uv.yx;

    float m = 0.;
    float zoom = mix(1., 5., fadeInt(110., T1, time));
    float kalei = I(T1, T2, time) + I(T2, T2+5., time)
                  +step(T2+5., time) * step(BEATS/8., mod(time+BEAT_OFF, BEATS/4.));
    float neigh = step(1.1, zoom);
    float tousle = S(60., 65., time)*I(60., T1, time)+step(T2+15., time)*kalei;
    float tousleA = mix(5., 10., fadeInt(60., T1, time));
    float nlines = mix(1., 10., S(10., 30., time));
    uv *= rot(.4*PI*fadeInt(110., T1, time));
    uv = mix(uv, vec2(fract(zoom*uv.x), uv.y), neigh);

    bass += 0.8*I(T1, T1+3.5, time);
    bass = bass*(1.-0.5*I(0., 30., time));
    bass = bass*(1.-0.2*I(0., T1, time));
    bass += 1.*I(T2, T2+1.5, time);
    float t = 2.*time*(1.-(kalei*.6*step(T2+2., time)));   // slow down on kaleidoscope
    vec2 uvO = uv;

    for(float off=-neigh; off<=neigh; off++) {
        uv = uvO + vec2(off, 0.);

        vec2 UV = uv;

        // kaleidoscope
        vec2 kv = abs(uv);
        kv *= rot(0.25*PI);
        kv = abs(kv);
        kv *= rot(0.6*PI+0.1*t);
        kv = abs(kv);

        uv = mix(UV, kv, kalei) ;

        for(float i=0.; i<nlines; i+=1.) {
            float s = sin(0.25*t)*i; 
            uv.x += bass*0.2*sin(s*2.+5.*uv.y-t);
            uv.x += 0.15*sin(20.*i+tousleA*uv.y+0.5*t)*tousle;
            m += abs(0.06/uv.x) * 0.01;                    // draw lines
        }
        m += abs((200./iResolution.x)/uv.x) * 0.02*bass;   // draw main line
        m += mid*0.02/(abs(uv.x)+abs(uv.y))*kalei;         // supernova
        m += mid*0.01/(abs(UV.x)+abs(UV.y))*kalei;
    }
    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);
    float bass = clamp(avgVolume(0, 40)-0.5, 0.01, 1.)*1.5;
    float mid = clamp(avgVolume(80, 100)-0.5, 0.0, 1.)*3.;
    vec2 sound = vec2(bass, mid);
    col += layer(uv, sound);

    if(time >= T2) {
    	col += vec3(layer(uv*0.75, sound), 0, 0);
    	col += vec3(0, layer(uv*0.5, sound), 0);
    	col += vec3(0, 0, layer(uv*0.25, sound));
	}

    col *= col;
    col = mix(1.-col, col, S(T2, T2+.2, time));

    fragColor = vec4(col,1.0);
}
