void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    vec2 pos1 = vec2(0.5,0.5+ sin(iTime/1.773)*0.1);
    float r1 = 0.35;

    vec2 pos2 = vec2(0.75 + sin(iTime/1.892)*0.3,0.6);
    float r2 = 0.20;

    vec2 pos3 = vec2(0.9,0.35);
    float r3 = 0.25;

    float d1 = distance(pos1,uv);
    float d2 = distance(pos2,uv);
    float d3 = distance(pos3,uv);

	float inf = 0.0;
    float flip = 2.0;

    inf += pow( max( (r1 - d1)/r1,0.), flip );
    inf += pow( max( (r2 - d2)/r2,0.), flip );
    inf += pow( max( (r3 - d3)/r3,0.), flip );

    inf = pow( inf, 1.0 / flip );  
    vec3 col = vec3(1,1,1) * inf;

    // Output to screen
    fragColor = vec4(col,1.0);
}
