float zoom = .4;

vec2 tf(vec2 pos) {
    return (pos - .5*iResolution.xy) / min(iResolution.x, iResolution.y) / zoom;
}

vec2 mul(vec2 a, vec2 b) {
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float seq(vec2 pos) {
    vec2 p = pos;
    int n = 100;

    int i = 0;
    for (; i < n; ++i) {
        p = mul(p, p) + tf(iMouse.xy);
        if (length(p) > 2.) {
            break;
        }
    }

    return float(n-i)/float(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;
    float s = seq(tf(fragCoord));
    if (s == 0.) {
        col = vec3(0.);
    } else {
    	col = hsv2rgb(vec3(s, 1., 1.-s));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
