highp float PI = 3.14159265359;

float elasticIn(float t) {
  return sin(13.0 * t * (PI/2.)) * pow(2.0, 10.0 * (t - 1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )   

{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float zoom = elasticIn(abs(sin(iTime * 100.)));

    float wiggle = sin(iTime * zoom);
    uv.x = (uv.x - wiggle);
    uv.y = (uv.y + wiggle);

    float scale = elasticIn(abs(wiggle));

    float strobe = abs(sin(iTime * (uv.x * uv.y * scale)));

    vec3 col = vec3 (strobe);

    // Output to screen
    fragColor = vec4(col,1.0);
}
