// Fork of "Pixelated rainbow clouds" by jarble. https://shadertoy.com/view/ttjXDK
// 2020-07-05 23:08:47

//this function is from https://www.shadertoy.com/view/4djSRW
vec3 hash32(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}


vec3 magnify(vec2 fragCoord,float mag){
	return hash32(floor(fragCoord*.3/pow(1.2,mag)));
}

vec3 pixel_above(vec2 fragCoord,float mag){
	return magnify(fragCoord+vec2(pow(2.,mag),0)*3.*iTime,mag);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color1 = vec3(0,0,0);
    float maximum = 5.0;
    for(float i = 1.0; i < 1.0+maximum; i++){
    	color1 += pixel_above(fragCoord,i+1.0);
    }
    fragColor = vec4(color1/maximum,0.0);
    fragColor.r = mix(fragColor.r, 1., max(0., 3.3 - 3.*fragColor.b - 4.*fragColor.g));
    fragColor.b += .2*fragColor.r;
    fragColor.g -= .1*fragColor.b;
}
