#define INTENSITY 16.
#define BORDERCOLOR vec3(.992, .843, .894)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 col = texture(iChannel0, uv).rgb;

    float vig = INTENSITY*uv.x * uv.y * (1.-uv.x)*(1.-uv.y);
    vig = pow(vig, .1 + .6 * fract(iTime * .0234));
    col = mix(BORDERCOLOR, col*vig, vig);
    // Output to screen
    fragColor = vec4(col,1.0);
}
