float cubicBounce(float t){
    float v = abs(t * 2.0 - 1.0);
    return v * v * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float t = cubicBounce(fract(iTime*0.5));

    float texel = 2.0/iResolution.y;

   	fragColor = vec4(smoothstep(0.0, texel, 1.0-uv.y-t));
}
