float Xor(float a, float b) {
	return a*(1.-b) + b*(1.-a);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float angle=iTime/10.;
    float s = .5*sin(angle);
    float c = .5*cos(angle);

    vec2 uv = (fragCoord-(.5)*iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0);
    uv *=mat2(c, s, s, -c);
    uv*=50.*s+10.;

    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float m = 0.;
    float t = iTime;

    for(float y=-1.;y<=1.;y++) {
        for(float x=-1.;x<=1.;x++) {
            vec2 offs = vec2(x,y);

            float d = length(gv-offs);
            //float dist = length(id+offs)*.3;
            float dist = length(id+offs)*100.;

            float r = mix(0.1,10.5,sin(dist-t)*.5+.5);
            m += smoothstep(r,r*.9,d);
            //m += Xor(m,smoothstep(r,r*.9,d));
    	}
    }

    //col.rg = gv/20.;    

    col += mod(m, 2.);
    col += mod(m, 7.)*.5*c;
    //col += mod(m, 7.)*c/.2;
    //col += mod(m, 2.);
    //col += mod(m, 8.);
    //col += m;


    fragColor = vec4(col, 1.0);

}
