// Что считать зелёным цветом
const float greenHsv = 0.33333333;

// Ширина полосы
const float colorMargin = 0.25;

/* ---- */
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

/* ---- */
vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

/* ---- */
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  // коэфф. десатурации
  // тот, который нужно экспортировать из движка
  // для наглядности будем менять со временем
  float desaturationIntensity = abs(sin(iTime));

  vec4 texColor = texture(iChannel1,fragCoord.xy/iResolution.xy);

  vec3 hsv = rgb2hsv(vec3(texColor.r, texColor.g, texColor.b)); 

  hsv.g = clamp(pow(hsv.r-0.333, 2.0)*5.0, 0.0, hsv.g);

   vec3 rgb = hsv2rgb(hsv);
   fragColor = vec4(rgb, texColor.a);
}
