vec3 addLine(vec2 uv, float t, vec3 col, float speed, float ustart, float ustop)
{
    float linepos = (ustart + ustop) * 0.5f + (ustop - ustart) * 0.5 * sin(2.f * 3.14f * speed * t);

    if (abs(uv.x-linepos) < 0.002f)
    {
        return vec3(0.0f, 0.0f, 0.0f);
    }
    else
    {
        return col;
    }
}

vec3 addLines(vec2 uv, float t, vec3 col)
{
    int NLINES = 1;
    for(int i=0; i<NLINES; i++)
    {
        col = addLine(uv, t, col,
                      0.3f + 0.2f * float(i), 
                      float(i)/float(NLINES) + 0.4f/float(NLINES), 
                      float(i)/float(NLINES) + 0.6f/float(NLINES));
    }

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col =vec3(uv.y, uv.y, uv.y);

    col = addLines(uv, iTime, col);


    // Output to screen
    fragColor = vec4(col,1.0);
}
