void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 dv =vec2(0.5,0.5) - uv;
    float dis = length(dv);

    float sinFactor =0.01*8.0 *sin(dis * 60.0 +iTime* -12.0) ;
    //use this to control ripple offset
    float rippleOffset=0.4;
    float discardFactor = clamp(0.2 - abs(rippleOffset - dis), 0.0, 1.0) / 0.2;

    vec2 offset = normalize(dv)* sinFactor * discardFactor;
     uv = offset + uv;
    fragColor= texture(iChannel0, uv); 
}
