float atz(vec3 p)
{
	vec3 a=abs(p);
	return max(min(max(max(
		min(length(vec2(mod(p.x+.067,.134)-.067,p.z)),max(a.x-.02,a.y)-.1),
		-min(length(p.xy-vec2(0,.03))-.06,max(a.x,abs(p.y+.02)-.03)-.04)
    ),abs(a.x+a.y)-.4),abs(abs(a.x+a.y)-.41)*.707+.01),a.z)-.02;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float e = 1.0 / min(iResolution.x, iResolution.y);
    vec2 uv = e * (fragCoord - 0.5 * iResolution.xy);

    if(iMouse.z < 1.0)
    {
    	float d = atz(vec3(uv, 0));   
        fragColor = vec4(smoothstep(e, -e, d));
        return;
    }

    const float fov = 5.0;
    vec3 rd = normalize(vec3(uv, fov));
    vec3 ro = vec3(0, 0, -fov);

    vec2 a = iMouse.xy - 0.5 * iResolution.xy;
    a *= acos(-1.0) / iResolution.y;
    vec2 s=sin(a), c=cos(a);
    mat2 rx = mat2(c.x,s.x,-s.x,c.x);
    mat2 ry = mat2(c.y,s.y,-s.y,c.y);
    ro.yz *= ry; rd.yz *= ry;
    ro.xz *= rx; rd.xz *= rx;

    int i=-4;
    float m, t=0.0;
    for(;++i<40;)if(t+=m=atz(ro+rd*t),m<1e-5)break;
    fragColor = 1.0 - vec4(i) / 40.0;
}
